const pictureSrc1 = 'data:image/jpeg;base64,';

const pictureSrc2 = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQibXPwSmZ0bA6TAYv5BIxCo6qr_DcGglpCCQ&usqp=CAU';

let img = document.getElementById('image');

function pressForward () {
  img.src = pictureSrc1;
}

function pressBack () {
  img.src = pictureSrc2;
}

